import React, { useEffect, useState, useMemo } from "react";
import { ethers } from 'ethers';
import {
    base64ContractAddress,
    base64ContractABI,
    sitnftContractAddress,
    sitnftContractABI
} from '../utils/constants';
import { useRef } from "react";
const { ethereum } = window;

export const ContractContext = React.createContext();

const provider = new ethers.providers.Web3Provider(ethereum);
const signer = provider.getSigner();

/** Get SITNFT Contract Instance*/
const getSITNFTContract = () => {
    const sitnftContract = new ethers.Contract(sitnftContractAddress, sitnftContractABI, signer);
    console.log(provider, signer, sitnftContract);
    return sitnftContract;
}

export const ContractProvider = ({ children }) => {
    const [mintData, setMintData] = useState({
        moduleCode: "",
        testType: "",
        grade: "",
        trimester: "",
        recipient: ""
    });
    const [mintResult, setmintResult] = useState('');

    function handleMint(evt) {
        const value = evt.target.value;
        setMintData({
            ...mintData,
            [evt.target.name]: value
        });
    }

    const mint = async () => {
        const {
            moduleCode,
            testType,
            grade,
            trimester,
            recipient
        } = mintData;

        const sitnftInstance = getSITNFTContract();
        try {
            console.log(
                moduleCode,
                testType,
                grade,
                trimester,
                recipient
            );
            const result = await sitnftInstance.mint(
                moduleCode,
                testType,
                grade,
                trimester,
                recipient
            );
            console.log(result);
        } catch (err) {
            console.error(err);
            return err;
        }
    }

    return (
        <ContractContext.Provider
            value={{
                mint,
                mintData,
                mintResult,
                handleMint
            }}>
            {children}
        </ContractContext.Provider >
    );
}
