import React, { useContext, useState } from "react";
import Card from 'react-bootstrap/Card';
import Placeholder from 'react-bootstrap/Placeholder';
import Button from 'react-bootstrap/Button';
import ListGroup from 'react-bootstrap/ListGroup';
import ListGroupItem from 'react-bootstrap/ListGroupItem';
import { Row, Col, Container } from "react-bootstrap";
// import Pagination from 'react-bootstrap/Pagination'

import { ContractContext } from '../../contexts/ContractProvider';
import Pagination from "../Pagination";

const MyGrades = () => {
  const [postsPerPage] = useState(3);
  const [currentPage, setCurrentPage] = useState(1);
  const {
    loading,
    setLoading,
    functGetAllTokens,
    transactionsResult
  } = useContext(ContractContext);

  const getTokens = (e) => {
    setLoading(true);
    functGetAllTokens();
  };

  const renderGrades = (e) => {
    return (
      <>
        {
          loading ? <h2 className="text-center">Loading...</h2> :
            null
        }
      </>
    );
  };

  // Get current posts
  const indexOfLastPost = currentPage * postsPerPage;
  const indexOfFirstPost = indexOfLastPost - postsPerPage;
  const currentPosts = transactionsResult.slice(indexOfFirstPost, indexOfLastPost);
  // Change page
  const paginate = pageNumber => setCurrentPage(pageNumber);

  return (
    <div className='page-two container'>
      <h2 className="pb-2 border-bottom text-start mt-5">MyGrades. Testing.</h2>
      <div className="d-flex row justify-content-around my-3 text-center">
        <div className="col-sm-12 text-center mb-5">
          <button className="btn btn-block btn-outline-primary mt-3" type="button" onClick={getTokens}>Get All Tokens</button>
        </div>
        <div className="d-flex justify-content-around my-3">
          Your Results:

          <Container>
            <Row>
              {
                loading ? <h2 className="text-center">Loading...</h2> :
                  null
              }
              {Object.values(currentPosts).map((val, k) => { renderGrades }
                
              )}
            </Row>
          </Container>
        </div>
      </div >
      <Pagination className="row col-sm-12 text-end"
        postsPerPage={postsPerPage}
        totalPosts={transactionsResult.length}
        paginate={paginate}
      />
      {/* <div className='mt-5 text-center'>
        <Pagination >
          <Pagination.First />
          <Pagination.Prev />
          <Pagination.Item>{1}</Pagination.Item>
          <Pagination.Ellipsis />
          <Pagination.Item>{3}</Pagination.Item>
          <Pagination.Item>{4}</Pagination.Item>
          <Pagination.Item active>{5}</Pagination.Item>
          <Pagination.Item>{4}</Pagination.Item>
          <Pagination.Ellipsis />
          <Pagination.Item>{8}</Pagination.Item>
          <Pagination.Next />
          <Pagination.Last />
        </Pagination>
      </div> */}
    </div >
  );
}

export default MyGrades;
